--DROP TABLE VXJCM008_PXJCO163;
CREATE OR REPLACE VIEW "vxjcm008_pxjco163"
    ("companycode","registeredperson","registereddt","updatedperson","updateddt",
    "hostname","programid","exclusivefg","deptgrp","jigyobu_cd","kanri_no",
    "edaban","shochishiji_no","shikakari_kotei_cd_mei","kc_hinmei","ko_su",
    "torihikisaki_cd","torihikisaki_mei","tape_lot_no","saishu_jisseki_nyuryoku_date",
    "seizo_lot_no","ido_lot_jotai","suryo_tani","sheet_su","kiban_su","sagyosha_name",
    "horyu_kbn")
AS
 SELECT a.companycode,
    a.registeredperson,
    a.registereddt,
    a.updatedperson,
    a.updateddt,
    a.hostname,
    a.programid,
    a.exclusivefg,
    a.deptgrp,
    a.jigyobu_cd,
    a.kanri_no,
    a.edaban,
    a.shochishiji_no,
    (max((a.shikakari_kotei_cd)::text) ||
        CASE
            WHEN (max((c.kotei_mei)::text) IS NULL) THEN ''::text
            ELSE (':'::text || max((c.kotei_mei)::text))
        END) AS shikakari_kotei_cd_mei,
    max((d.kc_hinmei)::text) AS kc_hinmei,
    sum(a.ko_su) AS ko_su,
    max((e.torihikisaki_cd)::text) AS torihikisaki_cd,
    max((e.torihikisaki_mei_ryaku)::text) AS torihikisaki_mei,
    max((a.tape_lot_no)::text) AS tape_lot_no,
    a.saishu_jisseki_nyuryoku_date,
    a.seizo_lot_no,
    a.ido_lot_jotai,
    max((c.suryo_tani)::text) AS suryo_tani,
    a.sheet_su,
    a.kiban_su,
    max((f.sagyosha_name)::text) AS sagyosha_name,
    a.horyu_kbn
   FROM (((((txjcm006 a
     JOIN ( SELECT txjcm006.companycode,
            txjcm006.deptgrp,
            txjcm006.kanri_no,
            max((txjcm006.edaban)::text) AS edaban
           FROM txjcm006
          GROUP BY txjcm006.companycode, txjcm006.deptgrp, txjcm006.kanri_no) b ON ((((a.companycode)::text = (b.companycode)::text) AND ((a.deptgrp)::text = (b.deptgrp)::text) AND ((a.kanri_no)::text = (b.kanri_no)::text) AND ((a.edaban)::text = b.edaban))))
     LEFT JOIN txjcm004 c ON ((((a.companycode)::text = (c.companycode)::text) AND ((a.deptgrp)::text = (c.deptgrp)::text) AND ((a.seizo_order_no)::text = (c.seizo_order_no)::text) AND ((a.so_no)::text = (c.so_no)::text) AND ((a.shochishiji_no)::text = (c.shochishiji_no)::text) AND ((a.shikakari_kotei_cd)::text = (c.kotei_cd)::text))))
     JOIN txjcm003 d ON ((((a.companycode)::text = (d.companycode)::text) AND ((a.deptgrp)::text = (d.deptgrp)::text) AND ((a.seizo_order_no)::text = (d.seizo_order_no)::text))))
     LEFT JOIN txjam007 e ON ((((d.companycode)::text = (e.companycode)::text) AND ((d.deptgrp)::text = (e.deptgrp)::text) AND ((d.torihikisaki_cd)::text = (e.torihikisaki_cd)::text))))
     LEFT JOIN vxjam034 f ON ((((a.companycode)::text = (f.companycode)::text) AND ((a.deptgrp)::text = (f.deptgrp)::text) AND ((a.updatedperson)::text = (f.sagyosha_cd)::text))))
  GROUP BY a.companycode, a.deptgrp, a.kanri_no, a.edaban, a.shochishiji_no, a.saishu_jisseki_nyuryoku_date, a.seizo_lot_no, a.ido_lot_jotai, a.sheet_su, a.kiban_su, a.updateddt, a.updatedperson;
;
