--DROP TABLE VXJCM137_AM026;
CREATE OR REPLACE VIEW VXJCM137_AM026
  (
    COMPANYCODE,
    REGISTEREDPERSON,
    REGISTEREDDT,
    UPDATEDPERSON,
    UPDATEDDT,
    HOSTNAME,
    PROGRAMID,
    EXCLUSIVEFG,
    DEPTGRP,
    JIGYOBU_CD,
    HINMOKU_CD,
    KAKOTEJUN_CD,
    KAKOTEJUN_REV,
    KOTEI_CD,
    KOTEI_NO,
    SEQ,
    SHOCHISHIJI_NO,
    SHIJI_KOTEI_CD,
    KOTEI_MEI,
    SHOCHISHIJI_REV,
    KOTEI_INJI_KBN,
    SO_NO
  )
AS
SELECT
M.COMPANYCODE
,M.REGISTEREDPERSON
,M.REGISTEREDDT
,M.UPDATEDPERSON
,M.UPDATEDDT
,M.HOSTNAME
,M.PROGRAMID
,M.EXCLUSIVEFG
,M.DEPTGRP
,M.JIGYOBU_CD
,M.HINMOKU_CD
,M.KAKOTEJUN_CD
,M.KAKOTEJUN_REV
,M.KOTEI_CD
,M.KOTEI_NO
,M.SEQ
,M.SHOCHISHIJI_NO
,M.KOTEI_CD AS SHIJI_KOTEI_CD
,S1.KOTEI_MEI
,CASE WHEN M.SHOCHISHIJI_NO = '0000' THEN '' ELSE M.SHOCHISHIJI_NO END AS SHOCHISHIJI_REV
,M.KOTEI_INJI_KBN
,M.SO_NO
FROM
(
SELECT
B.COMPANYCODE
,B.REGISTEREDPERSON
,B.REGISTEREDDT
,B.UPDATEDPERSON
,B.UPDATEDDT
,B.HOSTNAME
,B.PROGRAMID
,B.EXCLUSIVEFG
,B.DEPTGRP
,B.JIGYOBU_CD
,A.HINMOKU_CD
,B.KAKOTEJUN_CD
,B.KAKOTEJUN_REV
,B.KOTEI_CD
,B.KOTEI_NO
,0 AS SEQ
,B.SHOCHISHIJI_NO
,B.KOTEI_CD AS SHIJI_KOTEI_CD
,B.KOTEI_INJI_KBN
,B.SO_NO
FROM TXJCM137 A
LEFT JOIN TXJAM026 B
ON B.COMPANYCODE = A.COMPANYCODE
AND B.DEPTGRP = A.DEPTGRP
AND B.KAKOTEJUN_CD = A.KAKOTEJUN_CD
AND B.KAKOTEJUN_REV = A.KAKOTEJUN_REV
AND B.SO_NO = '000'
WHERE B.SHOCHISHIJI_NO = '0000'
UNION
-- 処置指示No ≠ "0000" (追加工程手順)
SELECT
C.COMPANYCODE
,C.REGISTEREDPERSON
,C.REGISTEREDDT
,C.UPDATEDPERSON
,C.UPDATEDDT
,C.HOSTNAME
,C.PROGRAMID
,C.EXCLUSIVEFG
,C.DEPTGRP
,C.JIGYOBU_CD
,D.HINMOKU_CD
,C.KAKOTEJUN_CD
,C.KAKOTEJUN_REV
,C.KOTEI_CD
,E.KOTEI_NO
,C.KOTEI_NO AS SEQ
,C.SHOCHISHIJI_NO
,D.SHIJI_KOTEI_CD
,C.KOTEI_INJI_KBN
,C.SO_NO
FROM TXJAM026 C
LEFT JOIN TXJCM137 D
ON D.COMPANYCODE = C.COMPANYCODE
AND D.DEPTGRP = C.DEPTGRP
--AND D.HINMOKU_CD = C.KAKOTEJUN_CD
AND D.KAKOTEJUN_CD = C.KAKOTEJUN_CD
AND D.SHOCHISHIJI_NO = C.SHOCHISHIJI_NO
LEFT JOIN (
	SELECT
	E2.COMPANYCODE
	,E2.DEPTGRP
	,E2.KAKOTEJUN_CD
	,E2.SHOCHISHIJI_NO
	,E2.KOTEI_CD
	,E2.KOTEI_NO
	FROM TXJCM137 E1
	INNER JOIN TXJAM026 E2
	ON E2.COMPANYCODE = E1.COMPANYCODE
	AND E2.DEPTGRP = E1.DEPTGRP
	AND E2.KAKOTEJUN_CD = E1.KAKOTEJUN_CD
	AND E2.KAKOTEJUN_REV = E1.KAKOTEJUN_REV
	AND E2.SO_NO = '000'
) E
ON E.COMPANYCODE = C.COMPANYCODE
AND E.DEPTGRP = C.DEPTGRP
AND E.KAKOTEJUN_CD = C.KAKOTEJUN_CD
AND E.SHOCHISHIJI_NO = '0000'
AND E.KOTEI_CD = D.SHIJI_KOTEI_CD
WHERE C.SHOCHISHIJI_NO <> '0000'
) M
LEFT JOIN TXJAM023 S1
ON S1.COMPANYCODE = M.COMPANYCODE
AND S1.DEPTGRP = M.DEPTGRP
AND S1.KOTEI_CD = M.KOTEI_CD
;

